import re
from BeautifulSoup import BeautifulSoup
import datetime, urllib
from django.contrib.auth.models import User
from tagging.models import Tag
from basic.blog.models import Post

AUTHOR = User.objects.get(id=2)
#AUTHOR = 'nphelan'

LIVE_STATUS = 2

ALLOW_COMMENTS = False

DATES = []


def get_blog_text(soup):
    string = ''
    for tag in soup.findAll(attrs={'class':'article'}):
        go = False
        for p in tag.findAll({'p':True}):
            if go == True:
                for item in p.contents:
                    string += str(item).replace('\xe2\x80\x9c', '"').replace('\xe2\x80\x99\\', "'").replace('\xe2\x80\x99', "'").replace("\\'", "'").replace('\xe2\x80\xa6.', ' ... ').replace('\xe2\x80\x9d', '"').replace('&nbsp;', ' ').replace('            ', ' ').replace('  ', ' ')
                string += '\n\n'
            go = True
    return string

def get_all_entries(urls=ARTICLE_URLS):
    author = AUTHOR
    list = []
    for url in ARTICLE_URLS:
        entry = {}
        now = datetime.datetime.now()
        file = urllib.urlopen(url['url'])
        parsed = get_blog_text(BeautifulSoup(file))
        if url['year']:
            if url['month']:
                date = datetime.datetime(int(url['year']), MONTHS_DICT[url['month']], 1)
            else:
                date = datetime.datetime(int(url['year']), 1, 1)
        else:
            date=datetime.datetime(2007, 1, 1)
        global DATES
        while True:
            if date in DATES:
                date += datetime.timedelta(weeks=1)
            else:
                DATES.append(date)
                break
        tags = ""
        for tag in TAGS:
            match = re.search(tag[1], parsed, re.I)
            if match:
                tags += '"'+tag[1]+'" '
        categories = []
        for category in CATEGORIES:
            match = re.search(category[1], parsed, re.I)
            if match:
                categories.append(category[0])

        entry['title'] = url['title']
        entry['slug'] = url['url'].rsplit('/', 1)[1].rsplit('.', 1)[0][:40]+'_'+date.strftime('%Y_%m_%d')
        entry['author'] = author
        entry['body'] = parsed
        entry['tease'] = parsed[:600]+' ...'
        entry['status'] = LIVE_STATUS
        entry['allow_comments'] = ALLOW_COMMENTS
        entry['publish'] = date
        entry['tags'] = tags
        post = Post(**entry)
        post.save()
        if categories:
            post.categories.add(*categories)
#        Tag.objects.update_tags(post, tags)
        list.append((post.id,post.title,post.publish,))
    return list

def get_article_urls():
    articles = []
    soup = BeautifulSoup(file)
    for tag in soup.findAll(attrs={'class':'article'}):
        d = {}
        h = ''
        for a in tag.findAll({'a': True, 'h3':True}):
            if a.name == 'h3':
                if a.a:
                    h = a.a.contents
                if len(h) > 0:
                    h = h[0]
            if a.name == 'a':
                y = ''
                m = ''
                title = ''
                year = re.search('\d{4}', a['href'])
                if year:
                    y = year.group(0)
                for month in MONTHS_LIST:
                    mth = re.search(month, a['href'])
                    if mth:
                        m = mth.group(0)
                if a.contents:
                    title = a.contents[0]
                articles.append(dict(url="http://www.harmonypbarchive.org/%s"%(a['href']), section=str(h), year=y, month=m, title=title,))

